import React, { useState, useEffect } from 'react';
import DataTable from 'react-data-table-component';

const baseURL = "http://localhost:8080/phones";

export default function PhoneTable({ queryString }) {
    const [data, setData] = useState([]);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const url = baseURL + "?" + queryString;
                console.log('Query:', queryString);
                console.log(url);
                const response = await axios.get(url);
                setData(response.data);
            } catch (error) {
                console.error('Error fetching data:', error);
            }
        };

        if (queryString) {
            fetchData();
        }
    }, [queryString]);
    const columns = [
        {
            name: "ID",
            selector: (row) => row.personID,
            sortable: true
        },
        {
            name: "Full Name",
            selector: (row) => row.fullName,
            sortable: true
        },
        {
            name: "Height",
            selector: (row) => row.height,
            sortable: true
        },
        {
            name: "eyeColor",
            selector: (row) => row.eyeColor,
            sortable: true
        },
    ];

    const rows = [
        {
            personID: 1,
            fullName: "Kate Shein",
            height: "1.79m",
            eyeColor: "blue",
        },
        //....remaining objects for the 2nd to 14th person
        {
            personID: 15,
            fullName: "Isabella Thompson",
            height: "1.79m",
            eyeColor: "blue",
        },
    ];

    const paginationOptions = {
        rowsPerPageText: 'Rows per page:',
        rangeSeparatorText: 'of',
        selectAllRowsItem: true,
        selectAllRowsItemText: 'All'
    };

    return (

        <div class="container">
            <DataTable
                columns={columns}
                data={rows}
                fixedHeader
                pagination
                paginationPerPage={10} // Set the number of rows per page
                paginationRowsPerPageOptions={[5, 10, 15, 20]} // Define the options for rows per page
                paginationComponentOptions={paginationOptions}
            />
        </div>
    );
}
