import React, { useState, useEffect } from 'react';
import axios from "axios";

const baseURL = "http://localhost:8080/phones";

export default function PhoneSearch() {
    const [queryString, setQueryString] = useState('');

    const handleSearch = (newQueryString) => {
        setQueryString(newQueryString);
    };

    return (
        <div>
            <h1>Phone Search</h1>
            <FilterCriteria onSearch={handleSearch} />
            <TableDisplay queryString={queryString} />
        </div>
    );
}

function FilterCriteria({ onSearch }) {
    const [brand, setBrand] = useState('');
    const [model, setModel] = useState('');
    const [priceOperator, setPriceOperator] = useState('');
    const [priceValue, setPriceValue] = useState('');
    const [storageOperator, setStorageOperator] = useState('');
    const [storageValue, setStorageValue] = useState('');
    const [color, setColor] = useState('');

    const handleSearch = () => {
        const queryParams = {
            brand,
            model,
            priceOperator,
            priceValue,
            storageOperator,
            storageValue,
            color
        };

        const queryString = Object.keys(queryParams)
            .map(key => {
                if (queryParams[key] !== '') {
                    return `${key}=${encodeURIComponent(queryParams[key])}`;
                }
                return null;
            })
            .filter(Boolean)
            .join('&');

        onSearch(queryString);
    };

    return (
        <div>
            <h2>Filter Criteria</h2>
            <div>
                <label htmlFor="brand">Brand:</label>
                <input type="text" id="brand" name="brand" value={brand} onChange={e => setBrand(e.target.value)} />
            </div>
            <div>
                <label htmlFor="model">Model:</label>
                <input type="text" id="model" name="model" value={model} onChange={e => setModel(e.target.value)} />
            </div>
            <div>
                <label htmlFor="price">Price:</label>
                <select value={priceOperator} onChange={e => setPriceOperator(e.target.value)}>
                    <option value="between">Between</option>
                    <option value="gle">>=</option>
                    <option value="equal">Equal to</option>
                    <option value="less">Less than</option>
                </select>
                <input type="text" id="price" name="price" value={priceValue} onChange={e => setPriceValue(e.target.value)} />
            </div>
            <div>
                <label htmlFor="storage">Storage:</label>
                <select value={storageOperator} onChange={e => setStorageOperator(e.target.value)}>
                    <option value="between">Between</option>
                    <option value="greater">Greater than</option>
                    <option value="equal">Equal to</option>
                    <option value="less">Less than</option>
                </select>
                <input type="text" id="storage" name="storage" value={storageValue} onChange={e => setStorageValue(e.target.value)} />
            </div>
            <div>
                <label htmlFor="color">Color:</label>
                <input type="text" id="color" name="color" value={color} onChange={e => setColor(e.target.value)} />
            </div>
            <button onClick={handleSearch}>Search</button>
        </div>
    );
}


function TableDisplay({ queryString }) {
    const [data, setData] = useState([]);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const url = baseURL + "?" + queryString;
                console.log('Query:', queryString);
                console.log(url);
                const response = await axios.get(url);
                setData(response.data);
            } catch (error) {
                console.error('Error fetching data:', error);
            }
        };

        if (queryString) {
            fetchData();
        }
    }, [queryString]); // Include queryString as a dependency

    if (!data) return null;

    return (
        <div>
            <h2>Table Display</h2>
            <table>
                <thead>
                    <tr>
                        <th>Brand</th>
                        <th>Model</th>
                        <th>Storage</th>
                        <th>Color</th>
                        <th>Price</th>
                    </tr>
                </thead>
                <tbody>
                    {data.map((item, index) => (
                        <tr key={index}>
                            <td>{item.brand}</td>
                            <td>{item.model}</td>
                            <td>{item.storage}</td>
                            <td>{item.color}</td>
                            <td>${item.price.toFixed(2)}</td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
}
