import React, { useState, useEffect } from 'react';
import PhoneTable from "./Table/PhoneTable.jsx"
import FilterInput from "./Filter/FilterInput.jsx"

export default function PhoneSearch() {
    const [queryString, setQueryString] = useState('');

    const handleSearch = (newQueryString) => {
        setQueryString(newQueryString);
    };

    return (
        <div>
            <h1>Phone Search</h1>
            <FilterInput onSearch={handleSearch} />
            <FilterCriteria onSearch={handleSearch} />
            <PhoneTable queryString={queryString} />
        </div>
    );
}

function FilterCriteria({ onSearch }) {
    const [brand, setBrand] = useState('');
    const [model, setModel] = useState('');
    const [priceComp, setPriceComp] = useState('equal');
    const [price, setPrice] = useState('');
    const [priceMin, setPriceMin] = useState('');
    const [priceMax, setPriceMax] = useState('');
    const [storageComp, setStorageComp] = useState('equal');
    const [storage, setStorage] = useState('');
    const [storageMin, setStorageMin] = useState('');
    const [storageMax, setStorageMax] = useState('');
    const [color, setColor] = useState('');

    useEffect(() => {
        if (priceComp !== 'between') {
            setPriceMin('');
            setPriceMax('');
        }
    }, [priceComp]);

    useEffect(() => {
        if (storageComp !== 'between') {
            setStorageMin('');
            setStorageMax('');
        }
    }, [storageComp]);

    const handleSearch = () => {
        const queryParams = {
            brand,
            model,
            priceComp: (priceMin === '' && priceMax === '' && price === '') ? '' : priceComp,
            price,
            priceMin,
            priceMax,
            storageComp: (storageMin === '' && storageMax === '' && storage === '') ? '' : storageComp,
            storage,
            storageMin,
            storageMax,
            color
        };

        const queryString = Object.keys(queryParams)
            .map(key => {
                if (queryParams[key] !== '') {
                    return `${key}=${encodeURIComponent(queryParams[key])}`;
                }
                return null;
            })
            .filter(Boolean)
            .join('&');

        onSearch(queryString);
    };

    return (
        <div>
            <h2>Filter Criteria</h2>
            <div>
                <label htmlFor="brand">Brand:</label>
                <input type="text" id="brand" name="brand" value={brand} onChange={e => setBrand(e.target.value)} />
            </div>
            <div>
                <label htmlFor="model">Model:</label>
                <input type="text" id="model" name="model" value={model} onChange={e => setModel(e.target.value)} />
            </div>
            <div>
                <label htmlFor="price">Price:</label>
                <select value={priceComp} onChange={e => setPriceComp(e.target.value)}>
                    <option value="between">Between</option>
                    <option value="gte">Greater than or Equal to</option>
                    <option value="equal">Equal to</option>
                    <option value="lte">Less than or Equal to</option>
                </select>
                {priceComp === 'between' && (
                    <>
                        <input type="text" id="priceMin" name="priceMin" placeholder="Min" value={priceMin} onChange={e => setPriceMin(e.target.value)} />
                        <input type="text" id="priceMax" name="priceMax" placeholder="Max" value={priceMax} onChange={e => setPriceMax(e.target.value)} />
                    </>
                )}
                {['gte', 'equal', 'lte'].includes(priceComp) && (
                    <input type="text" id="price" name="price" value={price} onChange={e => setPrice(e.target.value)} />
                )}
            </div>
            <div>
                <label htmlFor="storage">Storage:</label>
                <select value={storageComp} onChange={e => setStorageComp(e.target.value)}>
                    <option value="between">Between</option>
                    <option value="gte">Greater Than or Equal to</option>
                    <option value="equal">Equal to</option>
                    <option value="lte">Less than or Equal to</option>
                </select>
                {storageComp === 'between' && (
                    <>
                        <input type="text" id="storageMin" name="storageMin" placeholder="Min" value={storageMin} onChange={e => setStorageMin(e.target.value)} />
                        <input type="text" id="storageMax" name="storageMax" placeholder="Max" value={storageMax} onChange={e => setStorageMax(e.target.value)} />
                    </>
                )}
                {['gte', 'equal', 'lte'].includes(storageComp) && (
                    <input type="text" id="storage" name="storage" value={storage} onChange={e => setStorage(e.target.value)} />
                )}
            </div>
            <div>
                <label htmlFor="color">Color:</label>
                <input type="text" id="color" name="color" value={color} onChange={e => setColor(e.target.value)} />
            </div>
            <button onClick={handleSearch}>Search</button>
        </div>
    );
}
